log_con_4_week_mean_lum <- log(mean(IL17_week_four_in_vivo$`Avg Radiance [p/s/cm2/sr]`[1:9]));log_con_4_week_mean_lum_se <- log(sd(IL17_week_four_in_vivo$`Avg Radiance [p/s/cm2/sr]`[1:9])/sqrt(9))
