primary_tumor_growth_week_4 <- data.frame("post.surgery"=c(7,14,21,28),mean_lum=c(log_con_1_week_mean_lum,log_con_2_week_mean, log_con_3_week_mean_lum, log_con_4_week_mean_lum, log_IL17_1_week_mean_lum,log_IL17_2_week_mean_lum, log_IL17_3_week_mean_lum, log_IL17_4_week_mean_lum),lum_se=c(log_con_1_week_mean_lum_se,log_con_2_week_se,log_con_3_week_mean_lum_se, log_con_4_week_mean_lum_se, log_IL17_1_week_se,log_IL17_2_week_mean_lum_se, log_IL17_3_week_mean_lum_se, log_IL17_4_week_mean_lum_se),group=rep(c("con","IL17"),each=4))
p <- ggplot(data = primary_tumor_growth_week_4,mapping = aes(x=primary_tumor_growth_week_4$post.surgery,y=primary_tumor_growth_week_4$mean_lum,group=primary_tumor_growth_week_4$group,color=group))
