#sixth week of in vivo imaging for half million cell experiment
#whenever you move a file on the hard drive you need to reset the working directory in the files pane in order to reflect that change
#this will also maintain Github connectivity
IL17_week_six_in_vivo <- read_csv("~/Box/Metastasis_project/MPC3/Experiments/Orthotopic_luciferase_imaging/IL17_metastasis_study/half_million_cells_per_inoculum/DMC20171030093701/IL17_week_six_in_vivo.csv")
log_con_6_week_mean_lum <- log(mean(IL17_week_six_in_vivo$`Avg Radiance [p/s/cm_/sr]`[1:7]));log_con_6_week_mean_lum_se <- log(sd(IL17_week_six_in_vivo$`Avg Radiance [p/s/cm_/sr]`[1:7])/sqrt(7))
log_IL17_6_week_mean_lum <- log(mean(IL17_week_six_in_vivo$`Avg Radiance [p/s/cm_/sr]`[8:12]));log_IL17_6_week_mean_lum_se <- log(sd(IL17_week_six_in_vivo$`Avg Radiance [p/s/cm_/sr]`[8:12])/sqrt(5))
primary_tumor_growth_week_6 <- data.frame("post.surgery"=c(7,14,21,28,35,42),mean_lum=c(log_con_1_week_mean_lum,log_con_2_week_mean, log_con_3_week_mean_lum, log_con_4_week_mean_lum, log_con_5_week_mean_lum, log_con_6_week_mean_lum, log_IL17_1_week_mean_lum,log_IL17_2_week_mean_lum, log_IL17_3_week_mean_lum, log_IL17_4_week_mean_lum, log_IL17_5_week_mean_lum, log_IL17_6_week_mean_lum),lum_se=c(log_con_1_week_mean_lum_se,log_con_2_week_se,log_con_3_week_mean_lum_se, log_con_4_week_mean_lum_se, log_con_5_week_mean_lum_se, log_con_6_week_mean_lum_se, log_IL17_1_week_se,log_IL17_2_week_mean_lum_se, log_IL17_3_week_mean_lum_se, log_IL17_4_week_mean_lum_se, log_IL17_5_week_mean_lum_se, log_IL17_6_week_mean_lum_se),group=rep(c("con","IL17"),each=6))
p <- ggplot(data = primary_tumor_growth_week_6,mapping = aes(x=primary_tumor_growth_week_6$post.surgery,y=primary_tumor_growth_week_6$mean_lum,group=primary_tumor_growth_week_6$group,color=group))
p+geom_line(size=0.75)+geom_point(size=2)+theme_classic()+geom_errorbar(aes(ymax=primary_tumor_growth_week_6$mean_lum+lum_se,ymin=primary_tumor_growth_week_6$mean_lum-lum_se),size=0.75)+xlab("Days Post Inoculation")+ylab("Mean Radiance (log p/s/cm2/sr)")+scale_color_manual(values = cbbPalette)
